import React, { useState } from 'react';
 const Abnormality = () =>{
    const recordsData = [
        { id: 1, tag: 'Tag1', type: 'Dog', condition: 'Normal' },
        { id: 2, tag: 'Tag2', type: 'Cat', condition: 'Abnormal' },
        { id: 3, tag: 'Tag3', type: 'Bird', condition: 'Normal' },
        // Add more records as needed
      ];
    
      // State for filtering
      const [filter, setFilter] = useState('');
    
      // Filtered records based on the filter value
      const filteredRecords = recordsData.filter((record) =>
        record.tag.toLowerCase().includes(filter.toLowerCase())
      );

    return(
        <div>
        <div className="flex flex-wrap mb-4">
        <h2 className="text-2xl font-bold my-4">Abnormality table</h2>
        <h3 className="text-md font-semibold my-6">(Control Panel)</h3>
        </div>
        {/* Filter input */}
        <input
        type="text"
        placeholder="Filter by Tag"
        value={filter}
        onChange={(e) => setFilter(e.target.value)}
        className="mb-4 p-2 rounded-md border"
        />
        {/* Records table */}
        <div className="bg-slate-50 bg-opacity-40 mr-5 text-center rounded-2xl">
        <table className=" table-auto w-full">
        <thead>
            <tr>
            <th className="px-4 py-2">Tag</th>
            <th className="px-4 py-2">Type</th>
            <th className="px-4 py-2">Condition</th>
            </tr>
        </thead>
        <tbody>
            {filteredRecords.map((record) => (
            <tr key={record.id}>
                <td className="px-4 py-2">{record.tag}</td>
                <td className="px-4 py-2">{record.type}</td>
                <td className="px-4 py-2">{record.condition}</td>
            </tr>
            ))}
        </tbody>
        </table>
        </div>
        </div>
    )
}
export default Abnormality;
