import React, { useState } from 'react';
import { SidebarData } from '../data/SidebarData';
import { NavLink } from 'react-router-dom';

const Sidebar = () => {
  const activeLink = 'hover:bg-green-500 pl-7 h-14 flex justify-start items-center text-#ACACAC bg-green-500';
  const normalLink = 'hover:bg-green-500 pl-3 h-14 flex justify-start items-center text-#ACACAC';
  const imageSectionClass = 'pl-3 h-14 flex justify-start items-center text-#ACACAC';

  const [isDropdownOpen, setDropdownOpen] = useState(false);
  const [dropdownTitle, setDropdownTitle] = useState('');

  const toggleDropdown = (title) => {
    if (isDropdownOpen && dropdownTitle === title) {
      setDropdownOpen(false);
      setDropdownTitle('');
    } else {
      setDropdownOpen(true);
      setDropdownTitle(title);
    }
  };

  return (
    <>
      <nav>
        <div className="items-center p-2">
          <h1 className="ml-5 text-xl font-semibold">Admin Dashboard</h1>
        </div>
        <ul className="text-#ACACAC">
          {SidebarData.map((item, index) => (
            <li key={index}>
              {item.subItems ? (
                <>
                  <div
                    className={`block p-3 cursor-pointer ${imageSectionClass}`}
                    onClick={() => toggleDropdown(item.title)}
                  >
                    <div className="flex justify-start items-center space-x-3 text-md">
                      <span className={imageSectionClass}>{item.img}</span>
                      <span>{item.title}</span>
                      <span className="ml-2">
                        {isDropdownOpen && dropdownTitle === item.title ? (
                          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <path d="M12 11.828L9.17196 14.657L7.75696 13.243L12 8.99998L16.243 13.243L14.828 14.657L12 11.828Z" fill="yellow" />
                          </svg>
                        ) : (
                          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <path d="M12 12.172L14.828 9.34302L16.243 10.757L12 15L7.757 10.757L9.172 9.34302L12 12.172Z" fill="white" />
                          </svg>
                        )}
                      </span>
                    </div>
                  </div>
                  {isDropdownOpen && dropdownTitle === item.title && (
                    <ul className="pl-8">
                      {item.subItems.map((subItem, subIndex) => (
                        <li key={subIndex}>
                          <NavLink
                            to={subItem.path}
                            className={({ isActive }) =>
                              isActive ? activeLink : normalLink
                            }
                          >
                            <div className="flex justify-start items-center space-x-3 text-md">
                              <span className={isActive ? '' : imageSectionClass}>{subItem.img}</span>
                              <span>{subItem.title}</span>
                            </div>
                          </NavLink>
                        </li>
                      ))}
                    </ul>
                  )}
                </>
              ) : (
                <NavLink
                  to={item.path}
                  className={({ isActive }) =>
                    isActive ? activeLink : normalLink
                  }
                >
                  <div className="flex justify-start items-center space-x-3 text-md">
                    <span className={isActive ? '' : imageSectionClass}>{item.img}</span>
                    <span>{item.title}</span>
                  </div>
                </NavLink>
              )}
            </li>
          ))}
        </ul>
      </nav>
    </>
  );
};

export default Sidebar;
