import React from 'react';
import { SidebarData } from '../data/SidebarData';
import { NavLink } from 'react-router-dom';
import { useState } from 'react';
// import { Link } from 'react-router-dom';

const Sidebar = () => {
  const [isDropdownOpen, setDropdownOpen] = useState(false);
  const [dropdownTitle, setDropdownTitle] = useState('');

  const toggleDropdown = (title) => {
    if (isDropdownOpen && dropdownTitle === title) {
      setDropdownOpen(false);
      setDropdownTitle('');
    } else {
      setDropdownOpen(true);
      setDropdownTitle(title);
    }
  };
  return (
    // <aside className="w-64 h-screen bg-gray-800 text-white">
    //   <div className="flex items-center p-4">
    //     <h1 className="ml-2 text-xl font-semibold">Admin Dashboard</h1>
    //   </div>
    //   <nav className="mt-6">
    //     <ul>
    //       <li>
    //         <Link to="/Home/camera" className="block p-3 hover:bg-gray-700">
    //           Camera View
    //         </Link>
    //       </li>
    //       <li>
    //         <Link to="/Home/real-time" className="block p-3 hover:bg-gray-700">
    //           Real-time Display
    //         </Link>
    //       </li>
    //       <li>
    //         <Link to="/Home/record" className="block p-3 hover:bg-gray-700">
    //           Record and Analysis
    //         </Link>
    //       </li>
    //       <li>
    //         <Link to="/Home/account" className="block p-3 hover:bg-gray-700">
    //           Account
    //         </Link>
    //       </li>
    //     </ul>
    //   </nav>
    // </aside>
    <>
    <nav>
      <div className="items-center p-2">
        <h1 className="ml-5 text-xl font-semibold">Admin Dashboard</h1>
      </div>
      <ul className="text-white">
        {SidebarData.map((item, index) => (
          <li key={index}>
            {item.subItems ? (
              <>
                <div
                  className="mt-5 block p-3 hover:bg-gray-700 cursor-pointer"
                  onClick={() => toggleDropdown(item.title)}
                >
                  <div className="flex justify-start items-center space-x-3 text-md">
                    <span>{item.img}</span>
                    <span>{item.title}</span>
                    <span className="ml-auto">
                      {/* You can replace the following with your chevron-down SVG or Unicode character */}
                      {isDropdownOpen && dropdownTitle === item.title ? '▲' : '▼'}
                    </span>
                  </div>
                </div>
                {isDropdownOpen && dropdownTitle === item.title && (
                  <ul className="pl-8">
                    {item.subItems.map((subItem, subIndex) => (
                      <li key={subIndex}>
                        <NavLink
                          to={subItem.path}
                          className="block p-3 hover:bg-gray-700"
                        >
                          <div className="flex justify-start items-center space-x-3 text-md">
                          <span>{subItem.img}</span>
                          <span>{subItem.title}</span>
                          
                          </div>
                        </NavLink>
                      </li>
                    ))}
                  </ul>
                )}
              </>
            ) : (
              <NavLink to={item.path} className="mt-5 block p-3 hover:bg-gray-700">
                <div className="flex justify-start items-center space-x-3 text-md">
                  <span>{item.img}</span>
                  <span>{item.title}</span>
                </div>
              </NavLink>
            )}
          </li>
        ))}
      </ul>
    </nav>
  </>
);
};

export default Sidebar;
