    return(
        <div>
            {/* <section>
                
            </section>  */}
            <section className="grid grid-cols-12">
                
                    <div className="col-span-3 bg-slate-400 h-screen pl-2">
                        <Sidebar />
                    </div>
                    <div className="col-span-9">
                         <Header />
                    </div>
                    <section className="col-span-9 bg-green-500 h-screen pl-2">
                    
                        <Outlet />
                
                    

                    </section>
                 
                
            </section>
        </div>
    )
}

export default RootLayout;
