import React from 'react';
import { SidebarData } from '../data/SidebarData';
import { Link } from 'react-router-dom';

const Sidebar = ({ onLinkClick }) => {
  const handleLinkClick = (event, path, title) => {
    event.preventDefault(); // Prevent the default navigation
    onLinkClick(title); // Call the provided onLinkClick function
    // You can also navigate programmatically here if needed
    // For example, use React Router's useNavigate hook
  };

  return (
    // <aside className="w-64 h-screen bg-gray-800 text-white">
    //   <div className="flex items-center p-4">
    //     <h1 className="ml-2 text-xl font-semibold">Admin Dashboard</h1>
    //   </div>
    //   <nav className="mt-6">
    //     <ul>
    //       <li>
    //         <Link to="/Home/camera" className="block p-3 hover:bg-gray-700">
    //           Camera View
    //         </Link>
    //       </li>
    //       <li>
    //         <Link to="/Home/real-time" className="block p-3 hover:bg-gray-700">
    //           Real-time Display
    //         </Link>
    //       </li>
    //       <li>
    //         <Link to="/Home/record-and-analysis" className="block p-3 hover:bg-gray-700">
    //           Record and Analysis
    //         </Link>
    //       </li>
    //       <li>
    //         <Link to="/Home/account" className="block p-3 hover:bg-gray-700">
    //           Account
    //         </Link>
    //       </li>
    //     </ul>
    //   </nav>
    // </aside>
    <section>
      <div className="items-center p-2">
        <h1 className="ml-5 text-xl font-semibold">Admin Dashboard</h1>
      </div>
      <div className='text-white'>
        {
          SidebarData.map((item, index) => {
            return (
              <div key={index}>
                <Link
                  to={item.path}
                  className="mt-5 block p-3 hover-bg-gray-700"
                  onClick={(event) => handleLinkClick(event, item.path, item.title)}
                >
                  <span>{item.title}</span>
                </Link>
              </div>
            )
          })
        }
      </div>
    </section>
  );
};

export default Sidebar;
