import React, { useState } from 'react';

const Abnormality = () => {
  const recordsData = [
    { id: 1, tag: 'Tag1', type: 'Dog', condition: 'Normal' },
    { id: 2, tag: 'Tag2', type: 'Cat', condition: 'Abnormal' },
    { id: 3, tag: 'Tag3', type: 'Bird', condition: 'Normal' },
    // Add more records as needed
  ];

  // State for filtering
  const [filter, setFilter] = useState('');

  // Get unique animal names for dropdown options
  const animalNames = Array.from(new Set(recordsData.map((record) => record.type)));

  // Filtered records based on the filter value
  const filteredRecords = recordsData.filter((record) =>
    record.type.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div>
      <div className="flex flex-wrap mb-4">
        <h2 className="text-2xl font-bold my-4">Abnormality Table</h2>
        <h3 className="text-md font-semibold my-6">(Control Panel)</h3>
      </div>
      {/* Filter dropdown */}
      <div className="mb-4 relative">
        <label htmlFor="animalFilter" className="mr-2 font-semibold">
          <i className="fas fa-filter mr-1"></i> Filter by Animal:
        </label>
        <div className="relative inline-block text-left">
          <div>
            <span className="rounded-md shadow-sm">
              <button
                type="button"
                className="inline-flex justify-center w-full rounded-md border p-2 focus:outline-none focus:border-blue-500 transition duration-300 ease-in-out"
                id="options-menu"
                aria-haspopup="true"
                aria-expanded="true"
              >
                {filter ? filter : 'All Animals'}
                <i className="fas fa-caret-down ml-2"></i>
              </button>
            </span>
          </div>
          <div
            className="origin-top-right absolute right-0 mt-2 w-56 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 focus:outline-none"
            role="menu"
            aria-orientation="vertical"
            aria-labelledby="options-menu"
          >
            <div className="py-1" role="none">
              <button
                onClick={() => setFilter('')}
                className="block w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 hover:text-gray-900"
                role="menuitem"
              >
                All Animals
              </button>
              {animalNames.map((name) => (
                <button
                  key={name}
                  onClick={() => setFilter(name)}
                  className="block w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 hover:text-gray-900"
                  role="menuitem"
                >
                  {name}
                </button>
              ))}
            </div>
          </div>
        </div>
      </div>
      {/* Records table */}
      <div className="bg-slate-50 bg-opacity-40 mr-5 text-center rounded-2xl">
        <table className=" table-auto w-full">
          <thead>
            <tr>
              <th className="px-4 py-2">Tag</th>
              <th className="px-4 py-2">Type</th>
              <th className="px-4 py-2">Condition</th>
            </tr>
          </thead>
          <tbody>
            {filteredRecords.map((record) => (
              <tr key={record.id}>
                <td className="px-4 py-2">{record.tag}</td>
                <td className="px-4 py-2">{record.type}</td>
                <td className="px-4 py-2">{record.condition}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Abnormality;
