import React, { useState } from "react";
import Sidebar from "../components/Layout/Sidebar";
import CameraView from "../components/pagess/Camera-view";
import RealtimeDisplay from "../components/pagess/Real-time-Display";
import RecordAnalysis from "../components/Layout/Record-analysis";
import Account from "../components/pagess/Account";

const HomePage = () => {
  const [selectedLink, setSelectedLink] = useState(null);

  const handleSidebarLinkClick = (link) => {
    setSelectedLink(link);
  };

  return (
    <>
      <section>
        <div className="grid grid-cols-12">
          <div className="col-span-3 bg-slate-400 h-screen pl-2">
            <Sidebar onLinkClick={handleSidebarLinkClick} />
          </div>
          <div className="col-span-9 bg-green-500 h-screen pl-2">
            {selectedLink === "camera" && <CameraView />}
            {selectedLink === "real-time" && <RealtimeDisplay />}
            {selectedLink === "record" && <RecordAnalysis />}
            {selectedLink === "account" && <Account />}
          </div>
        </div>
      </section>
    </>
  );
};

export default HomePage;
